package com.projectreap.ProjectReap.controller;import com.projectreap.ProjectReap.entity.Appreciation;import com.projectreap.ProjectReap.entity.User;import com.projectreap.ProjectReap.pojo.AppreciatedData;import com.projectreap.ProjectReap.repository.UserRepository;import com.projectreap.ProjectReap.service.AppreciationService;import com.projectreap.ProjectReap.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import javax.servlet.http.HttpServletRequest;@Controllerpublic class AppreciationController {    @Autowired    AppreciationService appreciationService;    @Autowired    UserService userService;    @PostMapping("/appreciation")    public String submitAppreciationDetails(@ModelAttribute AppreciatedData appreciatedData, HttpServletRequest request){        System.out.println(appreciatedData);        appreciationService.save(appreciatedData,request);        appreciationService.handlingBadge(userService.getById(appreciatedData.getAppreciatedUser()));        return "redirect:/user/dashboard";    }}